INSERT INTO `author` (`id`, `name`, `avatar`, `show_home`, `description`, `created_date`, `updated_date`, `status`, `slug`) VALUES (101,'Alexandre Dumas','authors/Alexander.jpg',1,'<p><strong>Alexandre Dumas</strong>&nbsp;(1802 - 1870), c&ograve;n được gọi Alexandre Dumas cha (để ph&acirc;n biệt với con trai &ocirc;ng cũng l&agrave; một nh&agrave; văn) l&agrave; một nh&agrave; văn c&oacute; sức s&aacute;ng t&aacute;c mạnh mẽ. &Ocirc;ng để lại h&agrave;ng trăm t&aacute;c phẩm, trong đ&oacute; c&oacute; khoảng 100 tiểu thuyết, số c&ograve;n lại l&agrave; kịch, b&uacute;t k&iacute;, ph&oacute;ng sự, hồi k&iacute;. Những tiểu thuyết nổi tiếng của &ocirc;ng như&nbsp;<em>Ba người l&iacute;nh ngự l&acirc;m, B&aacute; tước Monte Cristo,&hellip;</em>&nbsp;gi&agrave;nh được sự h&acirc;m mộ của độc giả khắp thế giới từ hơn một thế kỉ nay.&nbsp;</p>\r\n','2021-03-27 15:37:20','2021-05-27 15:27:04',1,'alexandre-dumas-1622104024491'),(102,'Jordan Belfort','authors/Jordan_Belford.jpg',0,'<p><strong>Jordan Ross Belfort&nbsp;</strong>sinh ng&agrave;y 9/7/1962) l&agrave; một nh&agrave; diễn thuyết v&agrave; cựu m&ocirc;i giới chứng kho&aacute;n Mỹ. &Ocirc;ng bị kết &aacute;n về tội gian lận li&ecirc;n quan đến thao t&uacute;ng thị trường chứng kho&aacute;n v&agrave; điều h&agrave;nh một trung t&acirc;m tư vấn như một phần của một loại lừa đảo cổ phiếu. &Ocirc;ng đ&atilde; trải qua 22 th&aacute;ng trong nh&agrave; giam Li&ecirc;n bang trước khi được tha bổng trước thời hạn.&nbsp;&Ocirc;ng đ&atilde; kể lại cuộc đời m&igrave;nh trong cuốn hồi k&yacute; S&oacute;i gi&agrave; phố wall,&nbsp;v&agrave; được chuyển thể th&agrave;nh phim, ph&aacute;t h&agrave;nh năm 2013, đạo diễn bởi Mark Score&nbsp;&nbsp;đ&oacute;ng vai ch&iacute;nh &ndash; Jordan Belfort.</p>\r\n','2021-03-27 15:45:02','2021-05-26 15:09:49',1,'jordan-belfort-1619853902948'),(103,'Matt Wallaert ','authors/Matt.jpeg',0,'<p>Matt Wallaert l&agrave; một nh&agrave; khoa học h&agrave;nh vi, thiết kế c&aacute;c sản phẩm v&agrave; chương tr&igrave;nh gi&uacute;p mọi người sống hạnh ph&uacute;c hơn, khỏe mạnh hơn. Sau khi rời khỏi học viện, trở th&agrave;nh Nh&agrave; khoa học h&agrave;nh vi của Microsoft v&agrave; l&agrave; gi&aacute;m đốc tại Microsoft Ventures. Hiện l&agrave; Gi&aacute;m đốc H&agrave;nh vi của Clover Health, một chương tr&igrave;nh Medicare Advantage thay đổi m&ocirc; h&igrave;nh bảo hiểm bằng c&aacute;ch bẻ cong đường cong rủi ro bằng thay đổi h&agrave;nh vi.</p>\r\n','2021-03-27 17:25:46',NULL,1,'matt-wallaert-1619853909579'),(104,'Patricia Highsmith','authors/Patricia Highsmith.jpeg',0,'<p>Patricia Highsmith l&agrave; một tiểu thuyết gia người Mỹ v&agrave; nh&agrave; văn truyện ngắn nổi tiếng với những bộ phim kinh dị t&acirc;m l&yacute; của c&ocirc;, bao gồm cả năm tiểu thuyết của c&ocirc; dựa tr&ecirc;n nh&acirc;n vật của Tom Ripley.</p>\r\n','2021-03-27 17:51:37',NULL,1,'patricia-highsmith-1619853917049'),(105,'Masashi Kishimoto','authors/tacgia.jpeg',0,'<p>T&aacute;c phẩm đầu tay của Masashi l&agrave;&nbsp;Karakuri&nbsp;được anh gửi cho nh&agrave; xuất bản&nbsp;Shueisha&nbsp;v&agrave;o năm 1995. Bộ truyện n&agrave;y đ&atilde; đem về cho anh giải thưởng&nbsp;&quot;Hop Step Award&quot;&nbsp;h&agrave;ng th&aacute;ng của tập san&nbsp;Weekly Shōnen Jump&nbsp;v&agrave; mở ra con đường đầy triển vọng cho anh. Th&aacute;ng 11 năm&nbsp;1999, Naruto bắt đầu được đăng tr&ecirc;n&nbsp;Weekly Shōnen Jump. Hiện tại series ch&iacute;nh của bộ truyện Naruto đ&atilde; kết th&uacute;c với 700 chương ch&iacute;nh v&agrave; một số bộ ngoại truyện. Bộ truyện cũng đ&atilde; được chuyển thể th&agrave;nh hai bộ&nbsp;anime&nbsp;tổng cộng 720 tập,&nbsp;12 phần phim lẻ (movie) v&agrave; 8 phần OVA rất th&agrave;nh c&ocirc;ng. Hiện tại anh l&agrave; người cố vấn trực tiếp cho đội ngũ thực hiện series&nbsp;Boruto - Thế hệ kế thừa của Naruto.</p>\r\n','2021-04-17 16:54:13',NULL,1,'masashi-kishimoto-1619853924892');
