INSERT INTO `comments` (`id`, `book_id`, `user_id`, `star`, `title`, `content`, `date`, `like_comment`, `created_date`, `updated_date`, `status`) VALUES (35,155,29,4,'Rất hài lòng','Bóng hình bắt gặp đó, như đúng như sai. Những chuyện cũ đã quên đó, như hư như thực.\n\nMười dặm hoa đào chiếu rạng đôi mắt bi thương, nhưng chẳng thể nào quên đi được giây phút trông thấy gương mặt nàng trong quá khứ.\n\nQuá khứ, hiện tại, tương lai - ba kiếp nhân duyên của Dạ Hoa và Bạch Thiển, giữa mười dặm hoa đào mênh mông thắm sắc, từ nay chỉ còn hạnh phúc ngập tràn.','2021-05-02 14:20:08',1,'2021-05-02 14:20:08','2021-05-02 14:25:11',1),(36,153,29,4,'Quá tuyệt vời ^^','Tuy nhiên, với việc trừng trị lũ côn đồ rắp tâm mưu toan bắt cóc trẻ con, tôi đã chọc vào “ổ kiến lửa”, thế lực thứ ba của thế giới này, “tộc Elf”…\n\nVà, sàn diễn lần này chính là câu chuyện bước vào hình thái Tiến hóa kế tiếp của “tôi”!!!\n\nTập 5, “Tôi là Nhện đấy, có sao không?” chính thức được ra mắt quý độc giả.','2021-05-02 14:20:27',0,'2021-05-02 14:20:27','2021-05-02 14:20:27',1),(37,152,29,5,'Hay lắm','1/14 là một bộ tiểu thuyết dài kì, gồm 5 tập, phong cách tương tự như các bộ phim dài tập \"The lost\" và \"Prison Break\" nổi tiếng của Mỹ, tình tiết hấp dẫn và nghẹt thở; lại pha chút trinh thám và giả mã của Higashino Keigo. Đặc điểm lớn nhất của câu chuyện là mỗi chương sách, thậm chí là mỗi chi tiết, mỗi đoạn đều chứa đầy nghi hoặc, khiến người đọc một khi đã cầm cuốn sách lên sẽ không thể nào đặt xuống được nữa, phải đọc một mạch đến hết mới thôi. Còn mỗi phần kết của cuốn sách, đều để lại phía sau những nghĩ vấn không có lời đáp, khiến độc giả phải ngóng chờ tập tiếp theo.','2021-05-02 14:20:46',1,'2021-05-02 14:20:46','2021-05-02 14:20:49',1),(38,145,29,5,'Good','Nếu bạn đã sẵn sàng bước lên một tầm cao mới trong nghệ thuật bán hàng, thì còn chần chờ gì nữa mà chưa lật giở những trang sách xuất sắc của David Hoffeld','2021-05-02 14:21:20',3,'2021-05-02 14:21:20','2021-05-27 17:43:51',1),(39,157,29,5,'Xuất sắc','Sách hay cho trẻ :v','2021-05-02 14:21:47',1,'2021-05-02 14:21:47','2021-05-02 14:30:53',1),(41,145,43,3,'Rất hài lòng','Nếu bạn đã sẵn sàng bước lên một tầm cao mới trong nghệ thuật bán hàng, thì còn chần chờ gì nữa mà chưa lật giở những trang sách xuất sắc của David Hoffeld?','2021-05-02 14:24:17',2,'2021-05-02 14:24:17','2021-05-27 17:43:53',1),(44,151,43,5,'Hay lắm','Đây là một bộ tiểu thuyết trinh thám về nghệ thuật đánh lừa tâm ký do định kiến, đã bán hơn 128.000 bản tại Nhật, xếp thứ 10 giải thưởng văn học KONOMYS năm 2008 và được đề cử giải thưởng YAMAMOTO SHUGORO năm 2008.','2021-05-02 14:44:28',0,'2021-05-02 14:44:28','2021-05-02 14:44:28',1),(45,143,43,4,'Rất hài lòng','dạng bài trắc nghiệm và tự luận để các em thực hành, nâng cao kỹ năng sử dụng ngôn ngữ của mình. Hơn nữa trong mỗi cuốn sách lại có các bài kiểm tra giữa kỳ và cuối kì để học sinh làm quen với các dạng bài và củng cố được kiến thức mà mình đã học.','2021-05-02 14:44:55',0,'2021-05-02 14:44:55','2021-05-02 14:44:55',1),(46,153,28,5,'Ù uôi','Hay dã man con ngan','2021-05-02 14:49:29',1,'2021-05-02 14:49:29','2021-05-02 14:49:36',1),(47,137,28,3,'Rất hài lòng','Hay quá trời','2021-05-02 14:50:09',0,'2021-05-02 14:50:09','2021-05-02 14:50:09',1),(48,149,28,5,'Hay lắm','Hay lắm lắm luôn =))','2021-05-02 14:51:54',0,'2021-05-02 14:51:54','2021-05-02 14:51:54',1),(50,146,28,4,'Hay lắm','12 chương sách giống như 12 bước đi đầu tiên của bạn trên hành trình khám phá chuyên ngành quản trị kinh doanh. Dù bạn là một học viên đang theo học đúng chuyên ngành này hay là một doanh nhân khát khao tạo ra ý tưởng đột phá từ các lí thuyết căn bản, “MBA căn bản” vẫn sẽ là người bạn đồng hành lý tưởng nhất của bạn.\n\nMời bạn đón đọc.\n\nThông tin tác giả\nJordan Belfort\n\nJordan Ross Belfort sinh ngày 9/7/1962) là một nhà diễn thuyết và cựu môi giới chứng khoán Mỹ. Ông bị kết án về tội gian lận liên quan đến thao túng thị trường chứng khoán và điều hành một trung tâm tư vấn như một phần của một loại lừa đảo cổ phiếu. Ông đã trải qua 22 tháng trong nhà giam ...\nXem tất cả sách của tác giả\nXem thêm\nThông tin chi tiết','2021-05-02 14:51:05',0,'2021-05-12 16:32:00','2021-05-12 16:32:00',1);
